package com.gmo.yenpt1;

public class IfStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String sDay = "Sunday";
		int iDay = 7;
		if (sDay.equals("Sunday")) {
			System.out.println("Today is Sunday");
		}
		if (iDay == 7) {
			System.out.println("Today is Sunday");
		}
	}

}
